mapper.select:
select id ,goods_name as goodsName,price,audit_status as auditStatus,category1_id as category1Id,category2_id as category2Id,category3_id as category3Id from tb_goods


		and seller_id = #{sellerId}


		and goods_name like concat('%',#{goodsName},'%')


		and audit_status = #{auditStatus}


order by id asc
